CREATE VIEW `blog_view_article` AS select `t1`.`blar_id` AS `blar_id`,`t1`.`blar_title` AS `blar_title`,`t1`.`blar_abstract` AS `blar_abstract`,`t1`.`blar_author` AS `blar_author`,`t1`.`blar_from` AS `blar_from`,`t1`.`blar_clicks` AS `blar_clicks`,`t1`.`blar_status` AS `blar_status`,`t1`.`blar_create_time` AS `blar_create_time`,`t1`.`blar_modify_time` AS `blar_modify_time`,`t2`.`blar_content` AS `blar_content` from (`blog_article` `t1` join `blog_content` `t2`) where (`t1`.`blar_id` = `t2`.`blar_id`)